/* &#252;berpr&#252;fe, ob ans als Wahrscheinlichkeit interpretiert werden kann */
probabilityp(ans) := is(numberp(float(ans)) and ans >= 0 and ans <= 1)
/* runde x auf 3 Nachkommastellen */
dp(x) := decimalplaces(x, 3)
/* stelle x in wissenschaftlicher Notation dar */
SCI(x) := scientific_notation(x, 2)
/* Rundungsgenauigkeit bei numerischen Antworttests */
epsilon: 0.001

/* Sensitivit&#228;t */
sen: dp(rand_with_step(0.50, 0.99, 0.01))
/* Spezifit&#228;t */
spe: dp(rand_with_step(0.50, 0.99, 0.01))
/* Pr&#228;valenz */
pre: dp(rand_with_step(0.05, 0.95, 0.05))
/* Falsch-Positiv-Rate */
FPR: dp(1 - spe)
/* Falsch-Negativ-Rate */
FNR: dp(1 - sen)
/* positiver pr&#228;diktiver Wert */
PPV: sen * pre / (sen * pre + (1 - spe) * (1 - pre))
/* Falscherkennungsrate */
FDR: (1 - spe) * (1 - pre) / (sen * pre + (1 - spe) * (1 - pre))
/* Falschauslassungsrate */
FOR: (1 - sen) * pre / ((1 - sen) * pre + spe * (1 - pre))
/* negativer pr&#228;diktiver Wert */
NPV: spe * (1 - pre) / ((1 - sen) * pre + spe * (1 - pre))

/* Formulierung der Aufgabenstellungen */
FPR_list: [1, "Falsch-Positiv-Rate", FPR]
FNR_list: [2, "Falsch-Negativ-Rate", FNR]
PPV_list: [1, "den positiv pr&#228;diktiven Wert", PPV, "Er", "Der positiv pr&#228;diktive Wert"]
FDR_list: [2, "die Falscherkennungsrate", FDR, "Sie", "Die Falscherkennungsrate"]
FOR_list: [3, "die Falschauslassungsrate", FOR, "Sie", "Die Falschauslassungsrate"]
NPV_list: [4, "den negativ pr&#228;diktiven Wert", NPV, "Er", "Der negativ pr&#228;diktive Wert"]

/* Auswahl der Aufgabenstellungen */
textA: rand([FPR_list, FNR_list])
textB: rand([PPV_list, FDR_list, FOR_list, NPV_list])
textC: rand(delete(textB, [PPV_list, FDR_list, FOR_list, NPV_list]))

/* L&#246;sungen */
TansA: textA[3]
TansB: textB[3]
TansC: textC[3]
