/* &#252;berpr&#252;fe, ob ans als Wahrscheinlichkeit interpretiert werden kann */ probabilityp(ans) := is(numberp(float(ans)) and ans >= 0 and ans <= 1) /* runde x auf 3 Nachkommastellen */ dp(x) := decimalplaces(x, 3) /* stelle x in wissenschaftlicher Notation dar */ SCI(x) := scientific_notation(x, 2) /* Rundungsgenauigkeit bei numerischen Antworttests */ epsilon: 0.001
/* gebe Wahrscheinlichkeiten vor */ probS: dp(rand_with_step(0.80, 0.90, 0.01)) probWS: dp(rand_with_step(0.85, 0.95, 0.01)) probWSc: dp(rand_with_step(0.05, 0.15, 0.01))
/* berechne die &#252;brigen Wahrscheinlichkeiten */ probSc: dp(1 - probS) probWcS: dp(1 - probWS) probWcSc: dp(1 - probWSc) probSW: probWS * probS / (probWS * probS + probWSc * probSc) probScW: probWSc * probSc / (probWS * probS + probWSc * probSc) probSWc: probWcS * probS / (probWcS * probS + probWcSc * probSc) probScWc: probWcSc * probSc / (probWcS * probS + probWcSc * probSc)
/* Formulierung der Aufgabenstellungen */ listB1: [1, "um eine Spammail", probSW, "P(S|W)", "P(W|S)P(S)", probWS, probS] listB2: [2, "nicht um eine Spammail", probScW, "P(S^c|W)", "P(W|S^c)P(S^c)", probWSc, probSc] listC1: [1, "um eine Spammail", probSWc, "P(S|W^c)", "P(W^c|S)P(S)", probWcS, probS] listC2: [2, "nicht um eine Spammail", probScWc, "P(S^c|W^c)", "P(W^c|S^c)P(S^c)", probWcSc, probSc]
/* Auswahl der Aufgabenstellungen */ listB: rand([listB1, listB2]) listC: rand([listC1, listC2])
/* L&#246;sungen */ TansA1: probS TansA2: probSc TansA3: probWS TansA4: probWcS TansA5: probWSc TansA6: probWcSc TansA: [TansA1, TansA2, TansA3, TansA4, TansA5, TansA6] TansB: listB[3] TansC: listC[3]
