/* &#252;berpr&#252;fe, ob ans als Wahrscheinlichkeit interpretiert werden kann */ probabilityp(ans) := is(numberp(float(ans)) and ans >= 0 and ans <= 1) /* runde x auf 3 Nachkommastellen */ dp(x) := decimalplaces(x, 3) /* stelle x in wissenschaftlicher Notation dar */ SCI(x) := scientific_notation(x, 2) /* Rundungsgenauigkeit bei numerischen Antworttests */ epsilon: 0.001 /* vereinfache ganzzahlige Fakult&#228;ten nur bis factlim */ factlim: 10
/* Anzahl der Schubladen im Schrank */ n: rand_with_step(10, 13, 1) /* Anzahl der F&#228;cher pro Schublade */ k: rand_with_step(10, 13, 1) /* Anzahl der Packungen */ l: rand(10) + k +2 /* Anzahl der Medikamente */ m: rand_with_step(n + 1, n * k - 1, 1)
/* Formeln f&#252;r F&#228;cher- bzw. Urnenmodelle */ mZmR(N, n) := N^n oZmR(N, n) := N! / (N - n)! mZoR(N, n) := binomial(N + n - 1, n) oZoR(N, n) := binomial(N, n)
/* Abk&#252;rzungen f&#252;r Formeln */ mZmR_Formel: "\\(N^n\\)" oZmR_Formel: "\\(\\dfrac{N!}{(N - n)!}\\)" mZoR_Formel: "\\(\\dbinom{N + n - 1}{n}\\)" oZoR_Formel: "\\(\\dbinom{N}{n}\\)" tr(de,en):=castext("[[lang code='de']]{@de@}[[/lang]][[lang code='en']]{@en@}[[/lang]]"); /* Abk&#252;rzungen f&#252;r F&#228;chermodelle */ mZmR_Fach: tr("Verteilen unterscheidbarer Objekte mit Mehrfachbelegung","Distribution of distinguishable objects with multiple occupancy") oZmR_Fach: tr("Verteilen unterscheidbarer Objekte ohne Mehrfachbelegung","Distribution of distinguishable objects without multiple occupancy") mZoR_Fach: tr("Verteilen ununterscheidbarer Objekte mit Mehrfachbelegung","Distribution of indistinguishable objects with multiple occupancy") oZoR_Fach: tr("Verteilen ununterscheidbarer Objekte ohne Mehrfachbelegung","Distribution of indistinguishable objects without multiple occupancy")
/* Abk&#252;rzungen f&#252;r Urnenmodelle */ mZmR_Urne: tr("Ziehen mit Zur&#252;cklegen und mit Reihenfolge","pull with put back and with order") oZmR_Urne: tr("Ziehen ohne Zur&#252;cklegen und mit Reihenfolge","pull without put back and with order") mZoR_Urne: tr("Ziehen mit Zur&#252;cklegen und ohne Reihenfolge","pull with put back and without order") oZoR_Urne: tr("Ziehen ohne Zur&#252;cklegen und ohne Reihenfolge","pull without put back and without order")
/* L&#246;sungen */ TansA: mZoR(k, l) TansA11: [[1, false, mZmR_Fach], [2, false, oZmR_Fach], [3, true, mZoR_Fach], [4, false, oZoR_Fach]] TansA12: [[1, false, mZmR_Urne], [2, false, oZmR_Urne], [3, true, mZoR_Urne], [4, false, oZoR_Urne]] TansA2: [[1, false, mZmR_Formel], [2, false, oZmR_Formel], [3, true, mZoR_Formel], [4, false, oZoR_Formel]] TansB: mZoR(k, l - k) TansC: oZmR(n * k, m) /* Hinweis: Nur f&#252;r n * k <= 170 kann der numerische Wert von (n * k)! problemlos dargestellt werden. */ TansC11: [[1, false, mZmR_Fach], [2, true, oZmR_Fach], [3, false, mZoR_Fach], [4, false, oZoR_Fach]] TansC12: [[1, false, mZmR_Urne], [2, true, oZmR_Urne], [3, false, mZoR_Urne], [4, false, oZoR_Urne]] TansC2: [[1, false, mZmR_Formel], [2, true, oZmR_Formel], [3, false, mZoR_Formel], [4, false, oZoR_Formel]]
