/* render number as percentages */
texaspercent (e) := block([num: first (args (e))], sconcat(tex1(ev(num*100, simp)), "\\,\\%"));
texput(aspercent, texaspercent);

texqt(e) := block([q: first(args(e)), df: second(args(e))],
  sconcat("t_{", tex1(df), "; ", tex1(q), "}"));
texput(qt, texqt);

texqchisq(e) := block([q: first(args(e)), df: second(args(e))],
  sconcat(" \\chi^2_{", tex1(df), "; ", tex1(q), "}"));
texput(qchisq, texqchisq);

nn: 20;
mean: rand([171, 169]);
var: rand([1, 4]);
std: sqrt(var);
variance_given: rand([true, false]);
alpha: 0.05;

ta_t_quantile: quantile_student_t (1-alpha/2, nn - 1);
eepsilon: ta_t_quantile * sqrt(var / nn);
conf_interval_mu: [mean - eepsilon, mean +&#8239;eepsilon];

conf_interval_variance: [(nn-1)*var/quantile_chi2(1 - alpha/2, nn-1), (nn-1)*var/quantile_chi2(alpha/2, nn-1)];

Tans3: [
[1, false, "Wir k&#246;nnen sicher sein, dass der unbekannte Parameter \\(\\mu\\) in dem Konfidenzintervall liegt."], 
[2, true, sconcat("Wenn wir das von uns verwendete Verfahren immer wieder auf jedes mal neue Packungen anwenden, so wird der unbekannte Parameter \\(\\mu\\) in ", stack_disp(aspercent(1 - alpha), "i"), " aller F&#228;lle im Konfidenzintervall liegen.")]];

ta_formula: random_permutation([
[1, true, sconcat("\\(\\left[ \\bar{X} - t_{n-1;",
  1 - alpha/2, "} \\sqrt{\\frac{s_X^2}{n}}, \\bar{X} +&#8239;t_{n-1;",
  1 - alpha/2, "}\\sqrt{\\frac{s_X^2}{n}}\\right]\\)")],
[2, false, "\\(\\left[  \\bar{X} - 1.96 \\frac{\\sigma}{\\sqrt{n}}, \\bar{X} +&#8239;1.96 \\frac{\\sigma}{\\sqrt{n}}\\right]\\)"],
[3, false, sconcat("\\(\\left[ (n-1) \\frac{s_X^2}{\\chi^2_{n-1;&#8239;", 1 - alpha/2, "}}, 
(n-1) \\frac{s_X^2}{\\chi^2_{n-1;", alpha/2, "}}, \\right]\\)")],
[4, false, sconcat("\\(\\left[ \\bar{X} - t_{n-1;",
  1 - alpha/2, "} s_X, \\bar{X} +&#8239;t_{n-1;",
  1 - alpha/2, "} s_X\\right]\\)")]
]);

ta_confvar_formula: random_permutation([
[1, false, sconcat("\\(\\left[ \\bar{X} - t_{n-1;",
  1 - alpha/2, "} \\sqrt{\\frac{s_X^2}{n}}, \\bar{X} +&#8239;t_{n-1;",
  1 - alpha/2, "}\\sqrt{\\frac{s_X^2}{n}}\\right]\\)")],
[2, false, "\\(\\left[  \\bar{X} - 1.96 \\frac{\\sigma}{\\sqrt{n}}, \\bar{X} +&#8239;1.96 \\frac{\\sigma}{\\sqrt{n}}\\right]\\)"],
[3, true, sconcat("\\(\\left[ (n-1) \\frac{s_X^2}{\\chi^2_{n-1;&#8239;", 1 - alpha/2, "}}, 
(n-1) \\frac{s_X^2}{\\chi^2_{n-1;", alpha/2, "}}, \\right]\\)")],
[4, false, sconcat("\\(\\left[ \\bar{X} - t_{n-1;",
  1 - alpha/2, "} s_X, \\bar{X} +&#8239;t_{n-1;",
  1 - alpha/2, "} s_X\\right]\\)")]
]);
