probabilityp(p) := is(numberp(p) and p >= 0 and p <= 1);
zip(l1, l2) := if emptyp (l1) then [] else if emptyp (l2) then [] else cons ([first (l1), first (l2)], zip (rest (l1), rest (l2)));
/* question variables */
min_height: rand([2,3,4,5,6,7]); cdf(x) := 1 - x^(-2);
/* teacher answers */
Tans1: 1 - cdf(min_height); Tans2: random_permutation([ [1, false , "\\(e^{-2y} 1_{[0,\\infty)} (y) \\)"], [2, true, "\\((1-e^{-2y}) 1_{[0,\\infty)} (y) \\)"], [3, false, "\\((1-e^{2y}) 1_{[0,\\infty)} (y) \\)"], [4, false, "\\((1-(\\ln y)^{-2})1_{[0,\\infty)} (y) \\)"], [5, false, "\\(\\frac{1}{y} (1-(\\ln y)^{-2}) 1_{(0,\\infty)} (y) \\)"] ]);
ta_translateMath: random_permutation( zip([ P(X >= min_height), P(X >= min_height + 1), P(X > min_height + 1), P(X > min_height) ], [false, false, false, true]));
ta_complement: 1 - F(min_height);
ta_defnCdf: random_permutation( map(lambda([x], [x[1], x[2], sconcat(stack_disp(x[3], "i"), " f&#252;r alle \\(a\\).")]), [ [1, true, G(a) = P(Y <= a)], [2, false, G(a) = P(Y >= a)], [3, false, G(a) = P(Y = a)]]));
ta_solveX: random_permutation( zip(map(lambda([x], setify([x])), [ X <= exp(a), X <= exp(Y), X <= log(a), X > exp(a), X = exp(a) ]), [true, false, false, false, false]));
