/*Intervallgrenzen a und b:*/
aa: rand_with_prohib(-8,7,[0]);
bb: rand_with_prohib(aa+1,8,[0]);

/*Generiere EINE m&#246;gliche Musterl&#246;sung:*/
ta1: x^2/integrate(x^2,x,aa,bb);

/*&#220;berpr&#252;fe, on eine Funktion f eine rationale Funktion ist:*/
rationaleFunktionp(f):=polynomialp(ratnumer(ans1), [x]) and polynomialp(ratdenom(ans1), [x]);

/*Bestimme Extrema von f:*/
Extrema(f,a,b):= if diff(f,x)=0 then [[],[]] else block([L1,L2,L3,L4,L5],
    /*Nst. von f' in der Form [x=n1, x=n2, ...]:*/
    L1:solve(diff(f,x)=0,x),
    /*Nst. von f' in der Form [n1, n2, ...]:*/
    L2: map(rhs,L1), 
    /*Nur noch alle Nst. aus dem Intervall:*/
    L3: sublist(L2,lambda([t],is(t>=aa and t<=bb))), 
    /*Alle dieser Nst. als Liste in der Form  [n1, f(n1), f''(n1)]:*/
    L4: makelist(
        [L3[k],subst(L3[k],x,f),subst(L3[k],x,diff(diff(f,x),x))],
        k,1,length(L3)
    ),
    /* Aufteilung in Unterlisten, wobei die erste Unterliste alle Minima und die zweite alle Maxima in der o. g. Form enth&#228;lt:*/
    L5: [sublist(L4,lambda([t],t[3]>0)),sublist(L4,lambda([t],t[3]<0))], 
    return(L5)
);

/*Variable f&#252;r die erste Multiple-Choice-Aufgabe:*/
Liste_mc: [
    [1,true,"Es gilt \\(f(x)\\ge0\\) f&#252;r alle \\(x\\in \\mathbb{R}\\)"],
    [2,false,"Es existiert mindestens ein \\(x\\in \\mathbb{R}\\) mit \\(f(x)<0\\)"],
    [3,true,"Es gilt \\(\\int_{-\\infty}^{\\infty}f(x)\\, dx =1\\)"],
    [4,false,"Es gilt \\(\\int_{-\\infty}^{\\infty}f(x)\\, dx >1\\)"],
    [5,false,"Es gilt \\(\\int_{-\\infty}^{\\infty}f(x)\\, dx <1\\)"]
];
/*Variable f&#252;r die zweite Multiple-Choice-Aufgabe:*/
Liste_mc_2: [
    [1,true,"Es muss gelten \\(h(x)\\ge0\\) f&#252;r alle \\(x\\in \\mathbb{R}\\)"],
    [2,false,"Es muss mindestens ein \\(x\\in \\mathbb{R}\\) mit \\(h(x)<0\\) existieren"],
    [3,true,"Es muss gelten \\(\\int_{-\\infty}^{\\infty}h(x)\\, dx =1\\)"],
    [4,false,"Es muss gelten \\(\\int_{-\\infty}^{\\infty}h(x)\\, dx >1\\)"],
    [5,false,"Es muss gelten \\(\\int_{-\\infty}^{\\infty}h(x)\\, dx <1\\)"]
];

feedbackvariables.text:
/* === Erkl&#228;rung der Knoten: ===
Knoten 1: Ist die MC-Aufgabe komplett richtig?
Knoten 2: Anzahl der Optionen richtig?
Knoten 3: Wurden 1 und 2 gleichzeitig angekreuzt?
Knoten 4: Wurden 3 und 4, 3 und 5 oder 4 und 5 gleichzeitig angekreuzt?
*/

/*Definiere die Antwort als Funktion:*/
g(x):=ans1;

/*Liste: Zahlen aus dem Intervall zwischen aa und bb (in 1/500-Schritten) in ans1 eingesetzt:*/
L1:makelist(subst(aa+(bb-aa)*(k-1)/500,x,ans1),k,1,501); 

/* Gibt "true" zur&#252;ck, wenn die Eigenschaft der Nichtnegativit&#228;t ordnungsgem&#228;&#223; erf&#252;llt ist: */
nichtnegativ: is(length(sublist(L1,lambda([x], is(x<0))))=0);

/*Wert des Integrals:*/
integ: if numberp(integrate(g(x),x,aa,bb)) then integrate(g(x),x,aa,bb) else decimalplaces(quad_qag(g(x),x,aa,bb,6)[1],3);

/*Menge der richtigen Optionen wird anhand der Eigenschaften der eingegebenen Funktion adaptiv berechnet:*/
Menge_Richtig_TAns: block([L],
    L:[1,2,3,4,5],
    if not(nichtnegativ) then L:delete(1,L), /*Nichtneg.-Eigenschaft erf&#252;llt*/
    if nichtnegativ then L:delete(2,L), /*Nichtneg.-Eigenschaft nicht erf&#252;llt*/
    if not(integ=1) then L:delete(3,L), /*Integral-Eigenschaft erf&#252;llt*/
    if not(integ>1) then L:delete(4,L), /*Integral >1*/
    if not(integ<1) then L:delete(5,L), /*Integral <1*/
    return(setify(L))
);

/* Menge mit den von Student*in angekreuzten Optionen: */
Menge_Richtig_SAns: setify(ans_mc);

/* === Erkl&#228;rung der Knoten: ===
Knoten 1: Enth&#228;lt die Funktion eine andere Variable als x?
Knoten 2: Ist die Funktion rational?
Knoten 3: Ist die Integraleigenschaft erf&#252;llt?
Knoten 4: Ist die Nichtnegativit&#228;tseigenschaft erf&#252;llt?
Knoten 5: Sind beide Eigenschaften erf&#252;llt (grafisches Feedback)
Knoten 6: Welchen Wert hat die Hilfsvariable state?
*/

/*Definiere die Antwort als Funktion:*/
g(x):=ans1;

/*Liste der in der Antwort vorkommenden Variablen:*/
Listofvars: listofvars(ans1);
Listofvars_ohne_x: delete(x,Listofvars);

/*Liste: Zahlen aus dem Intervall zwischen aa und bb (in 1/500-Schritten) in ans1 eingesetzt:*/
L1:makelist(subst(aa+(bb-aa)*(k-1)/500,x,ans1),k,1,501); 

/* Gibt "true" zur&#252;ck, wenn die Eigenschaft der Nichtnegativit&#228;t ordnungsgem&#228;&#223; erf&#252;llt ist: */
nichtnegativ: is(length(sublist(L1,lambda([x], is(x<0))))=0);

/*Wert des Integrals:*/
integ: if numberp(integrate(g(x),x,aa,bb)) then integrate(g(x),x,aa,bb) else decimalplaces(quad_qag(g(x),x,aa,bb,6)[1],3);

/*Strings, die dem Feedback-Text hinzugef&#252;gt werden:*/
variablen_str_1: if length(Listofvars_ohne_x)>1 then "den Parametern" else "dem Parameter";
variablen_str_2: if length(Listofvars_ohne_x)>1 then "diese Parameter" else "diesen Parameter";
integ_text: if numberp(integrate(g(x),x,aa,bb)) then integrate(g(x),x,aa,bb) else sconcat("ann&#228;herungsweise \\(",decimalplaces(quad_qag(g(x),x,aa,bb,6)[1],3),"\\)");
