questionvariables.text:
/* This question uses the Maxima package distrib */

/* render number as percentages */
texaspercent (e) := block([num: first (args (e))], sconcat(tex1(ev(num*100, simp)), "\\,\\%"));
texput(aspercent, texaspercent);

texqf(e) := block([q: first(args(e)), df1: second(args(e)), df2: third(args(e))],
  sconcat("F_{", tex1(df1), ", ", tex1(df2), "; ", tex1(q), "}"));
texput (qf, texqf);

/* bars for means of X and Y*/
texput(Xbar, "\\bar{X}");
texput(Ybar, "\\bar{Y}");

mean_x: 26;
mean_y: 29;
variances: sort(rand_selection([8,9,10,12], 2));
var_x: variances[1];
var_y: variances[2];
size_x: 20;
size_y: 20;
niveau: 5/100;
alternative: rand({'onesided, 'twosided});

simp: true;
statistic: var_y / var_x;
critical: quantile_f(1 - niveau, size_y - 1, size_x - 1);
simp: false;

rejectionPlot: plot(
  [[discrete, [critical, critical], [0, 1]],
  pdf_f(x, size_y, size_x)],
  [x, 0, mean_f(size_y, size_x) + 6*var_f(size_y, size_x)],
  [xlabel, "x"],
  [ylabel, sconcat("F_{", size_y - 1, ", ", size_x - 1, "}(x)")]);

choices: block([vals, correct, incorrect],
  correct: [F = s_Y^2 / s_X^2],
  incorrect: [
  F = s_Y / s_X,
  T = (n-1)* s_X^2 / sigma_0^2,
  T = (Ybar - Xbar) / sqrt((1/m + 1/n) *s_P^2),
  X = sum((N[i] - n*theta[i])^2 / (n*theta[i]), i, 1, k)],
  vals: makelist(i, i, ev(length(correct) + length(incorrect), simp)),
  zip_with(lambda([a, b],
      [a, if a <= length(correct) then true else false, stack_disp(b,"id")]),
    vals, append(correct, incorrect)));

TAns1: random_permutation(choices);
TAns2: ev(statistic, simp);
TAns3: critical;
TAns4: random_permutation([["Hypothese verwerfen", if statistic >= critical then true else false ],
  ["Hypothese nicht verwerfen", if statistic < critical then true else false]]);
