questionvariables.text:
/* This question uses the Maxima package distrib */

/* render number as percentages */
texaspercent (e) := block([num: first (args (e))], sconcat(tex1(ev(num*100, simp)), "\\,\\%"));
texput(aspercent, texaspercent);

texqt(e) := block([q: first(args(e)), df: second(args(e))],
  sconcat("t_{", tex1(df), "; ", tex1(q), "}"));
texput(qt, texqt);

/* bars for means of X and Y*/
texput(Xbar, "\\bar{X}");
texput(Ybar, "\\bar{Y}");

size_x: 20;
size_y: 20;

means: sort(rand_selection([26, 28, 29], 2));
mean_x: means[1];
mean_y: means[2];

var_x: 8;
var_y: 12;

niveau: 5/100;

simp:true;
var_comb: block([tt1, tt2],
  tt1: (size_x - 1) / (size_x + size_y - 2),
  tt2: (size_y - 1) / (size_x + size_y - 2),
  tt1 * var_x + tt2 * var_y);
statistic: (mean_y - mean_x) / sqrt((1/size_x + 1/size_y) * var_comb);
critical: quantile_student_t(1 - niveau, size_x + size_y - 2);
simp: false;

rejectionPlot: block([nu: ev(size_x + size_y - 2, simp)],
  plot(
    [[discrete, [critical, critical], [0, 1]],
    pdf_student_t(x, nu)],
    [x, mean_student_t(nu) - 5*var_student_t(nu), mean_student_t(nu) + 5*var_student_t(nu)],
    [xlabel, "x"],
    [ylabel, sconcat("t_{", nu, "}(x)")]));

choices: block([vals, correct, incorrect],
  correct: [T = (Ybar - Xbar) / sqrt((1/m + 1/n) *s_P^2)],
  incorrect: [
  T = sum(X[i] - mu[0], i, 1, n)/sqrt(n * s_X^2),
  T = (n-1)* s_X^2 / sigma_0^2,
  F = s_Y^2 / s_X^2,
  X = sum((N[i] - n*theta[i])^2 / (n*theta[i]), i, 1, k)],
  vals: makelist(i, i, ev(length(correct) + length(incorrect), simp)),
  zip_with(
    lambda([a, b],
    [a, if a <= length(correct) then true else false, stack_disp(b,"id")]),
  vals, append(correct, incorrect)));

test_result: [
  ["Hypothese verwerfen", is(statistic >= critical)],
  ["Hypothese nicht verwerfen", is(statistic < critical)]];

Tans1: random_permutation (choices);
Tans2: statistic;
Tans3: critical;
Tans4: random_permutation (test_result);
