questionvariables.text:
/* Tweak the LaTeX rendering */
texput(Xbar, "\\bar{X}");
texput(Ybar, "\\bar{Y}");
texput(r_xy, "r_{x,y}");

texqt(e) := block([q: first(args(e)), df: second(args(e))],
  sconcat("t_{", tex1(df), "; ", tex1(q), "}"));
texput(qt, texqt);

texqchisq(e) := block([q : first(args(e)), df : second(args(e))],
  sconcat("\\chi^2_{", tex1(df), "; ", tex1(q), "}"));
texput(qchisq, texqchisq);
nn:  rand([20, 30, 40]);

mean_x: 7.5;/* nn * 9 / 10;*/
mean_y: 95;/*nn * 12;*/

corr_xy: -0.6;
eval_x: rand([6, 8]);

std_x: 3;
std_y: 20;/*mean_y / 4*/

var_x: std_x^2;
var_y: std_y^2;

slope: std_y / std_x * corr_xy;
intercept: mean_y - slope * mean_x;
est_var: (nn - 1)/(nn - 2) * (1 - corr_xy^2) * var_y;

conf_alpha: intercept + quantile_student_t(0.975, nn-2)*sqrt(est_var*((nn-1)*var_x + nn*mean_x^2)/(nn*(nn-1)*var_x))*[-1, 1];
conf_beta: slope + quantile_student_t(0.975, nn-2)*sqrt(est_var/((nn-1)*var_x))*[-1, 1];
conf_sigma2: (nn - 1)*(1 - corr_xy^2)*var_y*[1 / quantile_chi2(0.975, nn - 2), 1 / quantile_chi2(0.025, nn - 2)];

Tans1: intercept;
Tans2: slope;
Tans3: intercept + slope*eval_x;
Tans4: est_var;
Tans5: conf_alpha;
Tans6: conf_beta;
Tans7: conf_sigma2;

simp: false;
Tans_f: random_permutation(
  block([vals, correct, incorrect],
    correct: [T = s_y / s_x * r_xy * sqrt(((n-2)*s_x^2)/((1-r_xy^2)*s_y^2))],
    incorrect: [
    T = sum(X[i] - mu[0], i, 1, n)/sqrt(n * s_X^2),
    T = (n-1)* s_X^2 / sigma_0^2,
    F = s_Y^2 / s_X^2,
    T = (Ybar - Xbar) / sqrt((1/m + 1/n) *s_P^2)],
    vals: makelist(i, i, ev(length(correct) + length(incorrect), simp)),
    zip_with(
      lambda([a, b],
        [a, if a <= length(correct) then true else false, stack_disp(b,"id")]),
      vals, append(correct, incorrect))));
simp: true;
tr(de,en):=castext("[[lang code='de']]{@de@}[[/lang]][[lang code='en']]{@en@}[[/lang]]");
block([
  tQuantile: quantile_student_t(0.95, nn-2),
  statistic: std_y / std_x * corr_xy * sqrt((nn-2)*var_x / ((1 - corr_xy^2)*var_y))],
Tans_g1: statistic,
Tans_g2: random_permutation([
  [tr("verworfen","discarded"), is(statistic <= -tQuantile)],
  [tr("beibehalten","kept"), is(statistic > -tQuantile)]
]));

feedbackvariables.text:
tQuantile: ev(decimalplaces(quantile_student_t(0.975, nn-2), 2), simp);
estVarOneDecimal: ev(decimalplaces(est_var, 1), simp);
delta: sqrt(estVarOneDecimal/((nn-1)*std_x^2));
