texqchisq(e) := block([q : first(args(e)), df : second(args(e))],
  sconcat("\\chi^2_{", tex1(df), "; ", tex1(q), "}"));
texput(qchisq, texqchisq);

nn: /* rot gelb blau gr&#252;n orange braun */
     [ 33, 22,  39,  17,  47,    22 ];

kk: length (nn);
alpha: 0.05;

p1: 0.125;
p2: 0.25;
theta0: /* rot gelb blau gr&#252;n orange braun */
         [ p1, p1,  p2,  p1,  p2,    p1 ];
total: sum (nn[j], j, 1, kk);

statistic: sum ((nn[i] - total*theta0[i])^2 / (total * theta0[i]), i, 1, kk);
critical: quantile_chi2 (1 - alpha, kk - 1);

correct_ans: [
"Chi-Quadrat-Anpassungstest"
];

incorrect_ans: [
"Zweiseitiger Test f&#252;r den Erwartungswert bei einer Stichprobe",
"Einseitiger Test f&#252;r den Erwartungswert bei einer Stichprobe",
"Einseitiger Test f&#252;r die Varianz bei einer Stichprobe",
"Zweiseitiger Test f&#252;r die Varianz bei einer Stichprobe"
];

appropiate_tests: append (maplist (lambda([t], [t, true]), correct_ans),
                maplist (lambda ([t], [t, false]), incorrect_ans));

test_result: [
["Hypothese verwerfen", is(statistic >= critical)],
["Hypothese nicht verwerfen", is(statistic < critical)]];

Tans1: random_permutation (appropiate_tests);
Tans2: statistic;
Tans3: critical;
Tans4: random_permutation (test_result);
Tans5: 1 - cdf_chi2(statistic, kk - 1);

Tans1a: random_permutation([
[1, true, "\\(\\displaystyle \\sum_{i=1}^n \\frac{(n_i - n \\theta^{(0)}_i)^2}{n \\theta^{(0)}_i}\\)"],
[2, false, "\\(\\displaystyle \\sum_{i=1}^n \\frac{n_i - n \\theta^{(0)}_i}{n \\theta^{(0)}_i}\\)"],
[3, false, "\\(\\displaystyle \\sum_{i=1}^n \\frac{X_i -\\mu}{\\sqrt{n} \\sigma}\\)"],
[4, false, "\\(\\displaystyle \\frac{s_Y^2}{s_X^2}\\)"]
]);
