questionvariables.text:
/* Lebensdauer einer Fahrradlampe
wurde entwickelt von
Daniel Mei&#223;ner <daniel.meissner-i4k(at)ruhr-uni-bochum.de>
nach einer Idee von
Herold Dehling <herold.dehling(at)ruhr-uni-bochum.de>
an der Ruhr-Universit&#228;t Bochum.
Dieses Werk ist lizenziert unter einer Creative Commons Namensnennung-Weitergabe unter gleichen Bedingungen 4.0 International Lizenz. Um eine Kopie der Lizenz zu erhalten, besuchen Sie http://creativecommons.org/licenses/by-sa/4.0/.
SPDX-License-Identifier: CC-BY-SA-4.0
Technische Informationen:
Diese Aufgabe ist eine mehrteilige Aufgabe, deren einzelne Aufgabenteile mithilfe von JavaScript nacheinander sichtbar gemacht werden. Manchen Aufgabenteilen sind zus&#228;tzlich Hilfeschritte beigef&#252;gt, die Studierende schrittweise durch den Aufgabenteil f&#252;hren. */
probabilityp (x) := is(x >= 0 and x <= 1);
mean: rand([10, 12, 8]); minyears: rand([1/8, 1/4, 1/2])*mean;
pdf: 1/mean * exp(-x/mean);
Tans1: fullratsimp(int(pdf, x, minyears, inf)); Tans2: Tans1^4;
Tans1a: random_permutation([ [P(X > minyears), true], [P(X >= minyears), true], [P(X = minyears), false], [P(X < minyears), false], [P(X <= minyears), false] ]);
integrals: map (lambda ([x], [x[1], x[2], simplode (x[3])]), [ [1, true, ["\\(\\displaystyle\\int_{", minyears, "}^\\infty", stack_disp(1/mean, ""), stack_disp(exp(-x/mean),""), "dx\\)"]], [2, false, ["\\(\\displaystyle\\int_{-\\infty}^{", minyears, "}", stack_disp(1/mean, ""), stack_disp(exp(-x/mean),""), "dx\\)"]], [3, false, ["\\(\\displaystyle\\int_{", minyears, "}^\\infty", stack_disp(1/mean, ""), stack_disp(x,""), stack_disp(exp(-x/mean),""), "dx\\)"]], [4, false, ["\\(\\displaystyle\\int_",0,"^{", minyears, "}", stack_disp(1/mean, ""), stack_disp(exp(-x/mean),"") ,"dx\\)"]] ]);
Tans1b: random_permutation(integrals);
Tans2a: random_permutation([ [1, true, sconcat("\\(\\{", stack_disp(X_1 > minyears, ""), ", \\dots, ", stack_disp(X_4 > minyears, ""), "\\}\\)")], [2, false, sconcat("\\(\\{", stack_disp(X_1 = minyears, ""), ", \\dots, ", stack_disp(X_4 = minyears, ""), "\\}\\)")], [3, false, sconcat("\\(\\{", stack_disp(X_1 < minyears, ""), ", \\dots, ", stack_disp(X_4 < minyears, ""), "\\}\\)")] ]);
Tans2b: random_permutation([ [1, true, sconcat( "\\(P(", stack_disp(X_1 > minyears, ""), ", \\dots, ", stack_disp(X_4 > minyears, ""), ") = P(", stack_disp(X_1 > minyears, ""), ") \\cdots P(", stack_disp(X_4 > minyears, ""), ")\\)")], [2, false, sconcat( "\\(P(", stack_disp(X_1 > minyears, ""), ", \\dots, ", stack_disp(X_4 > minyears, ""), ") = P(", stack_disp(X_1 > minyears, ""), ") + \\cdots + P(", stack_disp(X_4 > minyears, ""), ")\\)")], [3, true, sconcat( "\\(P(", stack_disp(X_1 > minyears, ""), ", \\dots, ", stack_disp(X_4 > minyears, ""), ") = P(", stack_disp(X_1 > minyears, ""), ")^4\\)")] ]);

feedbackvariables.text:
selection: setify(ans1a);
correct_solutions: setify(mcq_correct(Tans1a));
none_correct: emptyp(intersection(selection, correct_solutions));

selection: setify(ans2b);
correct_solutions: setify(mcq_correct(Tans2b));
partially_correct: subsetp(selection, correct_solutions);
none_correct: emptyp(intersection(selection, correct_solutions));
