/* utilities */

probabilityp(p) := is(numberp(p) and p >= 0 and p <= 1);

/* actual question variables */

bb: rand([3, 4, 5, 6]);
mm: -2/bb^2;
pdf(x) := -mm*(bb - x);
density_supportp(x) := is(numberp(x) and x >= 0 and x <= bb);

lo: bb - rand([1, 2]);
hi: lo + 1;

/* first char of value string indicates correct/false limits,
 * second char of value string indicates correct/false integrand
 *
 * E. g.: "ft" means that the limits are correct, whereas
 * the density is wrong
 */
integrals: map (lambda ([x], [x[1], x[2], simplode (x[3])]), [
  ["ft", false, ["\\(\\int_", 0, "^", bb, stack_disp(pdf(x), ""), "dx\\)"]],
  ["tt", true, ["\\(\\int_",lo,"^", hi, stack_disp(pdf(x), ""), "dx\\)"]],
  ["tf", false, ["\\(\\int_",lo,"^", hi, stack_disp(x*pdf(x), ""), "dx\\)"]],
  ["ff", false, ["\\(\\int_",0,"^", bb, stack_disp(x*pdf(x), "") ,"dx\\)"]]
]);

expected_integrals: map (lambda ([x], [x[1], x[2], simplode (x[3])]), [
  ["ft", false, ["\\(\\int_{-\\infty}^{\\infty}", stack_disp(x*pdf(x), ""), "dx\\)"]],
  ["tt", true, ["\\(\\int_",0,"^", bb, stack_disp(x*pdf(x), ""), "dx\\)"]],
  ["tf", false, ["\\(\\int_",0,"^", bb, stack_disp(pdf(x), ""), "dx\\)"]],
  ["ff", false, ["\\(\\int_{-\\infty}^{\\infty}", stack_disp(pdf(x), ""), "dx\\)"]]
]);

TAns1: int(pdf(x), x, lo, hi);
TAns2: int(x*pdf(x), x, 0, bb);

questionnote.text:
b: {# bb #}, lo: {# lo #}
Wahrscheinlichkeit: {# TAns1 #}
Erwartungswert {# TAns2 #}
