/* Wahrscheinlichkeitsfunktion einer binomialverteilten Zufallsvariablen: */
binomial_vert(k, n, p):= binomial(n,k)*p^k*(1-p)^(n-k);

/* Generiere zun&#228;chst zuf&#228;llig den Erwartungswert und darauf basierend dann n und p: */
EX: rand([2,3]);
n1: if EX=3 then rand([10,12,15,20,25,30,50,60,75,100]) else rand([10,20,25,40,50,100]);
p1: decimalplaces(EX/n1,2);

/* F&#252;r das Autocomplete-Eingabefeld in Aufgabenteil (a): */
opt1: sconcat("Bin(",n1,",",p1,")");
opt1_1: sconcat("Bin(",n1,",",1-p1,")");
opt2_1: sconcat("Hypergeometrisch(",n1, ",100,10)");
opt2_2: sconcat("Hypergeometrisch(",p1, ",100,",n1,")");
opt2_3: sconcat("Hypergeometrisch(",1-p1, ",100,",n1,")");
opt3_1: sconcat("Geometrisch(",p1,")");
opt3_2: sconcat("Geometrisch(", p1, ",", n1, ")");
opt3_3: sconcat("Geometrisch(",1-p1,")");
opt3_4: sconcat("Geometrisch(", 1-p1, ",", n1, ")");
opt4: sconcat("Gamma(", n1, ",", p1, ")");
opt5_1: sconcat("Poisson(", if float(round(n1*p1))=float(n1*p1) then round(n1*p1) else decimalplaces(n1*p1,2), ")");
opt5_2: sconcat("Poisson(", if float(round((1-p1)*n1))=float((1-p1)*n1) then round(n1*(1-p1)) else decimalplaces(n1*(1-p1),2), ")");
opt6_1: sconcat("Bernoulli(",n1, ",", p1, ")");
opt6_2: sconcat("Bernoulli(",n1, ",", 1-p1, ")");
opt6_3: sconcat("Bernoulli(", p1, ")");
opt6_4: sconcat("Bernoulli(", 1-p1, ")");
Optionen: set(opt1, opt1_1, opt2_1, opt2_2, opt2_3, opt3_1, opt3_2, opt3_3, opt3_4, opt4, opt5_1, opt5_2, opt6_1, opt6_2, opt6_3, opt6_4);

/* M&#246;glicher Fehler, dass beim Erwartungswert mit 1-p1 gerechnet wurde: */
EX_f: n1*(1-p1);

/* Varianz von X: */
Var: n1 * p1 * (1 - p1);

/* Wahrscheinlichkeit aus Aufgabenteil (c): */
TA4: 1 - sum(binomial_vert(k, n1, p1),k,0,EX-1);

/* Optionen f&#252;r die Single-Choice-Frage zum Gegenereignis: */
Liste2: [
    [1, true," \\(1-P(A)\\)"],
    [2, false," \\(\\frac{1}{P(A)}\\)"],
    [3, false, "\\((P(A))^c\\)"],
    [4, false," \\( \\frac{\\#A}{\\#\\Omega} \\)"]
];
Liste2: random_permutation(Liste2);

feedbackvariables.text:
/* == Erkl&#228;rung der Knoten: ===
Knoten 1: Wurde eine der Autocomplete-Optionen ausgew&#228;hlt?
