Knoten 3: opt1_1: sconcat("Bin(",n1,",",1-p1,")");
Knoten 4: opt5_1 und opt5_2 (Poisson)
Knoten 5: opt3_1: sconcat("Geometrisch(",p1,")") und opt3_3: sconcat("Geometrisch(",1-p1,")");
Knoten 6: opt3_2: sconcat("Geometrisch(", p1, ",", n1, ")") und opt3_4: sconcat("Geometrisch(", 1-p1, ",", n1, ")");
Knoten 7: opt4: sconcat("Gamma(", n1, ",", p1, ")");
Knoten 8: opt6_3: sconcat("Bernoulli(", p1, ")") und opt6_4: sconcat("Bernoulli(", 1-p1, ")");
Knoten 9: opt2_1, opt2_2, opt2_3 (Hypergeometrisch)
Knoten 10: opt6_1: sconcat("Bernoulli(",n1, ",", p1, ")") und opt6_2: sconcat("Bernoulli(",n1, ",", 1-p1, ")");
