/* utilities */

probabilityp(p) := is(numberp(p) and p >= 0 and p <= 1);

/* actual question variables */

pp: 9/10;
nn: rand([400, 900]);

mean: nn * pp;
variance: nn * pp * (1 - pp);
std: sqrt(variance);

max_missing: mean + rand([1, 2])*std;

Tans1: random_permutation([
  [simplode(["Bin(", nn, ", ", pp, ")-Verteilung"]), true],
  [simplode(["Exp(", nn * pp, ")-Verteilung"]), false],
  [simplode(["Poisson(", nn * pp, ")-Verteilung"]), false],
  [simplode(["N(", nn, ", ", pp, ")-Verteilung"]), false]
]);
Tans1b: binomial(nn,k)*pp^k*(1-pp)^(nn-k);
Tans2: mean;
Tans3: variance;
Tans4: decimalplaces(cdf_normal(max_missing, mean, std), 2);
