questionvariables.text:
/* Diese Aufgabe verwendet das Maxima-Paket distrib */

probabilityp (x) := is ((0 <= x) and (x <= 1));

/* Tweak LaTeX rendering */

/* Use Prob to enter the model distribution */
texput (Prob, texProb);
texProb(e) := block([theta, event],
  [theta, event]: args(e),
  sconcat("P_{", tex1(theta), "}(", tex1(event), ")"));

/* render number as percentages */
texaspercent (e) := block([num: first (args (e))],
  sconcat(tex1(ev(num*100, simp)), "\\,\\%"));
texput(aspercent, texaspercent);

/* Question variables */

sampleSize : 20;
defectRatioThreshold : 0.05;
trashThreshold : 3;
actualDefectRatio : 0.2;
truncateK : 6;
sampleX : 4;

help_k: 2;

/* Teacher answers */

/* (a) Statistical model */

tans1: random_permutation(
  block([vals, num_vals, correct, incorrect],
    correct: [P[theta] = Bin(sampleSize, theta)],
    incorrect: [
    P[theta] = N(sampleSize, theta),
    P[theta] = Ber(theta)
    ],
    num_vals: length(correct) + length(incorrect),
    vals: makelist(i, i, num_vals),
    zip_with(lambda([a, b],
        [a, if a <= length(correct) then true else false, stack_disp(b, "id")]),
      vals, append(correct, incorrect))));

/* (b) Hypothesis and alternative */

tans2a: [["&#8804;", true], ["=", false], ["&#8805;", false], [">", false], ["<", false]];
tans2b: [["&#8804;", false], ["=", false], ["&#8805;", false], [">", true], ["<", false]];

/* (c) Type 1 error */

type1Error: 1 - cdf_binomial(trashThreshold - 1, sampleSize, defectRatioThreshold);

tans3: decimalplaces(type1Error, 3);
tans3h1: random_permutation([
  [1, true, sconcat("\\(\\displaystyle \\alpha = \\sup_{\\theta\\in\\Theta_0} ", stack_disp(Prob(theta, X >= trashThreshold), ""), "\\)")],
  [2, true, "Der Fehler erster Art besteht darin, die Hypothese zu verwerfen, obwohl sie zutrifft."],
  [3, true, sconcat("Die Produktionscharge wird entsorgt, obwohl die tats&#228;chliche Fehlerquote ", defectRatioThreshold, " betr&#228;gt.  Die Qualit&#228;tskontrolle macht dabei einen Fehler erster Art.")],
  [4, false, sconcat("\\(\\alpha = \\sup_{\\theta\\in\\Theta_0} ", stack_disp(Prob(theta, X < trashThreshold), ""), "\\)")],
  [5, false, "Der Fehler erster Art besteht darin, die Hypothese nicht zu verwerfen, obwohl die Alternative zutrifft."],
  [6, false, sconcat("Die Produktionscharge wird nicht entsorgt, obwohl die tats&#228;chliche Fehlerquote gr&#246;&#223;er als ", defectRatioThreshold, " ist. Die Qualit&#228;tskontrolle macht dabei einen Fehler erster Art.")]]);
tans3h2: binomial(sampleSize, k) * defectRatioThreshold^k * (1 - defectRatioThreshold)^(sampleSize - k),simp:false;

/* (d) Power of the test */

tans4: decimalplaces(1 - cdf_binomial(trashThreshold - 1, sampleSize, actualDefectRatio), 2);
tans4h1: random_permutation([
[1, false, stack_disp(beta = Prob(defectRatioThreshold, X >= trashThreshold), "id")],
[2, false, stack_disp(beta = Prob(defectRatioThreshold, X < trashThreshold), "id")],
[3, true, stack_disp(beta = Prob(actualDefectRatio, X >= trashThreshold), "id")],
[4, false, stack_disp(beta = Prob(actualDefectRatio, X < trashThreshold), "id")]
]);
tans4h2: subst([p = actualDefectRatio, n=sampleSize], binomial(n, k) * p^k * (1-p)^(n-k)),simp:false;

/* (e) p-value */

tans5: decimalplaces(1 - cdf_binomial(sampleX - 1, sampleSize, defectRatioThreshold), 2);
tans5h1: random_permutation([
[1, true, sconcat("\\( p = \\sup_{\\theta \\in \\Theta_0} ", stack_disp(Prob(theta, X>=trashThreshold), ""), "\\)")],
[2, false, sconcat("\\( p = \\sup_{\\theta \\in \\Theta_0} ", stack_disp(Prob(theta, X<trashThreshold), ""), "\\)")],
[3, false, sconcat("\\( p = \\sup_{\\theta \\in \\Theta_1} ", stack_disp(Prob(theta, X<trashThreshold), ""), "\\)")],
[4, false, sconcat("\\( p = \\sup_{\\theta \\in \\Theta_1} ", stack_disp(Prob(theta, X>=trashThreshold), ""), "\\)")],
[5, false, sconcat("\\( p = \\sup_{\\theta \\in \\Theta_1} ", stack_disp(Prob(actualDefectRatio, X>=trashThreshold), ""), "\\)")]
]);
tans5h2: subst([p = defectRatioThreshold, n=sampleSize], binomial(n, k) * p^k * (1-p)^(n-k)),simp:false;
