/* Funktion f&#252;r das Runden auf zwei Dezimalstellen: */
dp(x):=decimalplaces(x,2);
/*dp(x):=float(x);*/

/* Funktion f&#252;r das arithmetische Mittel der Werte einer Liste: */
mean2(L):=apply("+",L)/length(L);

/* Funktion f&#252;r Liste ohne doppelte Werte: */
unique_list(min,max,anzahl):= block([L],
    L:[],
    for i:1 thru anzahl do(
        L: cons(rand_with_prohib(min,max,L),L)
    ),
return(L)
); 

/*Starte mit einer Geraden:*/
m: 4;
b: 8;
f(x):=m*x+b;

/*Anzahl der Datenpunkte:*/
laenge: 5;

/*Erzeuge x-Werte:*/
Lx: random_permutation(
	unique_list(20,32,laenge)
); 

/*Erzeuge y-Werte (entlang der Geraden mit zuf&#228;lligen Fehlern):*/
Ly: makelist(
    floor(
        rand_with_prohib(-15,15,[2,1,0,-1,-2])+ f( Lx[k] )
    ), k, 1, laenge
);

/*Daten zusammengefasst f&#252;r Aufgabenhinweis:*/
Daten: [Lx, Ly];

/*Arithmetisches Mittel (Musterl&#246;sung Aufgabenteil (a)):*/
AMx: mean2(Lx);
AMy: mean2(Ly);

/*Summe aller x- bzw. y-Werte:*/
AMx_sum: AMx*length(Lx);
AMy_sum: AMy*length(Ly);

/*Musterl&#246;sung f&#252;r Aufgabenteil (b):*/
Lx_mAM: makelist(Lx[i]-AMx,i,1,laenge);
Ly_mAM: makelist(Ly[i]-AMy,i,1,laenge);

/*Musterl&#246;sung f&#252;r Aufgabenteil (c):*/
TAns_tabelle3_x: map(lambda([x],x^2),Lx_mAM);
TAns_tabelle3_y: map(lambda([x],x^2),Ly_mAM);

/*Musterl&#246;sung f&#252;r Aufgabenteil (d):*/
TAns_tabelle4: makelist(Lx_mAM[i]*Ly_mAM[i],i,1,laenge);

/*Spaltensummen (Musterl&#246;sung f&#252;r Aufgabenteil (e)):*/
sumx: apply("+",TAns_tabelle3_x);
sumy: apply("+",TAns_tabelle3_y);
sumxy: apply("+",TAns_tabelle4);

/*Korrelationskoeffizient (Musterl&#246;sung f&#252;r Aufgabenteil (f)):*/
corr: sumxy/(sqrt(sumx*sumy));

/* Parameter beta (Musterl&#246;sung f&#252;r Aufgabenteil (g)):*/
beta: sumxy/sumx;

/* Parameter alpha (Musterl&#246;sung f&#252;r Aufgabenteil (h)):*/
alpha: AMy-beta*AMx;

/*Rundungsfehler von vorher mit einberechnet:*/
alpha_rf: decimalplaces(AMy,2)-decimalplaces(beta,2)*decimalplaces(AMx,2) ;
alpha_rf: decimalplaces(alpha_rf,2);

