%emode:false;

/* Anzahl gedruckter Nachkommastellen: */
fpprintprec:4;

/* Funktion f&#252;r Runden auf zwei Nachkommastellen: */
rnd(x):=decimalplaces(x,2);

/* Sterbewahrscheinlichkeit: */
p1: 0.01;

/* Stichprobengr&#246;&#223;e: */
n1: rand_with_step(200,600,50);

/* Anzahl aus Aufgabenteil (e): */
m1: 4;

tr(de,en):=castext("[[lang code='de']]{@de@}[[/lang]][[lang code='en']]{@en@}[[/lang]]");
/* Optionen f&#252;r Frage nach der Verteilung: */
TA1_Liste: [
    [tr("Laplace-Verteilung","laplace distribution"),false],
    [tr("Bernoulli-Verteilung","bernoulli distribution"),false],
    [tr("Binomialverteilung","binomial distribution"),true],
    [tr("hypergeometrische Verteilung","hypergeometric distribution"),false],
    [tr("Poisson-Verteilung","poisson distribution"),true],
    [tr("geometrische Verteilung","geometric distribution"),false]
];

/*
    Diese Aufgabe kann sowohl mit der Binomial- als auch mit der Poission-
    Verteilung gel&#246;st werden. Im Folgenden werden daher L&#246;sungen f&#252;r beide
    Varianten definiert:
*/

/*
    === Erste L&#246;sungsm&#246;glichkeit: ===
*/

/* Verteilung: */
bin: tr("Binomialverteilung","binomial distribution");

/* Wahrscheinlichkeitsfunktion: */
P1(k):=binomial(n1,k)*p1^k*(1-p1)^(n1-k);

/* Erwartungswert: */
EW: n1*p1;

/* Varianz: */
Var: n1*p1*(1-p1);

/* Wahrscheinlichkeit aus Aufgabe (e): */
P1_1: 1-sum(P1(k),k,0,m1-1);
P1_1_falsch: sum(P1(k),k,0,m1-1);

/* Wahrscheinlichkeit aus Aufgabe (f): */
P2_1: P1(0);

/*
    === Zweite L&#246;sungsm&#246;glichkeit: ===
*/


/* Verteilung: */
pois: tr("Poisson-Verteilung","poisson distribution");

/* Parameter der Poisson-Verteilung = EW = Var: */
lambda: if integerp(n1*p1) then round(n1*p1) else n1*p1;

/* Wahrscheinlichkeitsfunktion: */
P2(k):=exp(-1*lambda)*lambda^k/k!;

/* Wahrscheinlichkeit aus Aufgabe (e): */
P1_2: 1-sum(P2(k),k,0,m1-1);
P1_2_falsch: sum(P2(k),k,0,m1-1);

/* Wahrscheinlichkeit aus Aufgabe (f): */
P2_2: P2(0);

feedbackvariables.text:
/* === Erklärung der Knoten: ===
Knoten 1: Wurde im ersten Aufgabenteil die Binomialverteilung ausgewählt?
Knoten 2: Stimmt der EW (Binomialverteilung)?
Knoten 3: Stimmt der EW (Poisson-Verteilung)?
*/

/* Absoluter Abstand zwischen Antwort und korrektem EW (für weniger fehleranfälligen Vergleich): */
diff_ew_bin: abs(float(ans2)-float(EW));
diff_ew_pois: abs(float(ans2)-float(lambda));

/* === Erklärung der Knoten: ===
Knoten 1: Wurde im ersten Aufgabenteil die Binomialverteilung ausgewählt?
Knoten 2: Stimmt die Varianz (Binomialverteilung)?
Knoten 3: Wurde fälschlicherweise die Varianz für die Poisson-Verteilung angegeben?
Knoten 4: Stimmt die Varianz (Poisson-Verteilung)?
Knoten 5: Wurde fälschlicherweise die Varianz für die Binomialverteilung angegeben?
*/


/* Absoluter Abstand zwischen Antwort und korrekter Varianz (für weniger fehleranfälligen Vergleich): */
diff_var_bin: abs(float(ans3)-float(Var));
diff_var_pois: abs(float(ans3)-float(lambda));

/* === Erklärung der Knoten: ===
Knoten 1: Wurde im ersten Aufgabenteil die Binomialverteilung ausgewählt?
Knoten 2: Stimmt die W-Funktion (Binomialverteilung)?
Knoten 3: Wurde fälschlicherweise die W-Funktion der Poisson-Verteilung angegeben?
Knoten 4: Stimmt die W-Funktion (Poisson-Verteilung)?
Knoten 5: Wurde fälschlicherweise die W-Funktion der Binomialverteilung angegeben?
*/

/* Absoluter Abstand zwischen Antwort und korrekter Funktion (für weniger fehleranfälligen Vergleich): */
diff_wfun_bin: abs(ans1-P1(k));
diff_wfun_pois: abs(ans1-P2(k));
