/* utility functions */

probabilityp (x) := is(numberp (x) and x >= 0 and x <= 1);
normalizedp (list) := is(lsum(x, x, list) = 1);

check_list_items (list, predicate) := block ([false_list: [], max: length(list)],
  for i: 1 step 1 thru max do
    if (not (predicate(list[i])))
    then false_list: cons(i, false_list),
  false_list
);

/* [List-of ITEM] -> ITEM
 *
 * Return index of first occurence of ELEMENT in LIST or false if no
 * such element.
 */
find_index(list, element) := block([indices],
  indices: sublist_indices(list, lambda([x], is(x = element))),
  if emptyp(indices) then false else indices[1]);

/* Matrix [List-of Number] [List-of Number] -> Matrix
 *
 * Compute the probability vector of max(X, Y) where X and Y are
 * random variables with values X_VALUES resp. Y_VALUES with joint
 * distribution given by JOINT_DIST.
 */
dist_maximum(joint_dist, x_values, y_values) := block([
  union_values,
  x_set,
  y_set
  ],
  x_set: setify(x_values),
  y_set: setify(y_values),
  matrix(
    makelist(
      if member(a, intersection(x_set, y_set))
      then
      (lsum(joint_dist[find_index(x_values, a), j],
          j, sublist_indices(y_values, lambda([y], y <= a))) +
        lsum(joint_dist[j, find_index(y_values, a)],
          j, sublist_indices(x_values, lambda([x], x < a))))
      elseif member(a, x_set)
      then
      lsum(joint_dist[find_index(x_values, a), j], j,
        sublist_indices(y_values, lambda([y], y <= a)))
      else
      /* i is in y_set */
      lsum(joint_dist[j, find_index(y_values, a)],
        j, sublist_indices(x_values, lambda([x], x <= a))),
      a, listify(union(x_set, y_set)))));

/* actual question variables */

x_values: [0, 1, 2, 3];
y_values: [0, 1, 2, 3];

prob: rand([1/5, 1/3, 1/2])
pp: [prob, (1 - prob)/3, (1-prob)/3, (1-prob)/3];

pdf(k) := if member(k, [0, 1, 2, 3]) then pp[k + 1] else 0;
pdf_sum(k) := sum(pdf(i)*pdf(k - i), i, 0, 3);

Tans1: apply (matrix, makelist (makelist (pdf(i)*pdf(j), j, 0, 3), i, 0, 3));

Tans_a_1: random_permutation([
[P(X=k, Y=l) = P(X=k) * P(Y=l), true],
[P(X=k, Y=l) = P(X=k) + P(Y=l), false],
[P(X=k, Y=l) = P(X=l) * P(Y= k), false],
[P(X=k, Y=l) = P(X=l) + P(Y=k), false]
]);

Tans_a_2: pdf(1) * pdf(1);

Tans2: sum((k - Tans1)^2 * pdf(k), k, 0, 3);
Tans3: pdf_sum(0);
Tans4: pdf_sum(1);
Tans5: pdf_sum(2);
Tans6: pdf_sum(3);
Tans7: pdf_sum(4);
Tans8: pdf_sum(5);
Tans9: pdf_sum(6);

Tans_b_1: { [3, 0], [2, 1], [1, 2], [0, 3] };
Tans_b_2: pdf_sum(3);

Tans_c: dist_maximum(Tans1, x_values, y_values);

Tans_c_1: { [2, 0], [2, 1], [2, 2], [1, 2], [0, 2] };
Tans_c_2: Tans_c[1,3];

feedbackvariables.text:
/* Knoten 2: alle Einträge im Intervall [0, 1] */
/* Knoten 3: Zeilensumme ist im Intervall [0, 1]? */
/* Knoten 4: Spaltensumme ist im Intervall [0, 1]? */

non_probability_entries: sublist_indices(apply (append, args (ans1)),
                                                                lambda([aij], not(probabilityp(aij))));

sum_is_not_probabilityp(list) := not(probabilityp (lsum (x, x, list)));
non_normalized_rows: sublist_indices (args (ans1), sum_is_not_probabilityp);
non_normalized_cols: sublist_indices (args (transpose (ans1)), sum_is_not_probabilityp);
