questionvariables.text:
/* This question uses the Maxima package distrib */

/* Tweak LaTeX rendering of expressions */

/* render number as percentages */
texaspercent (e) := block([num: first (args (e))], sconcat(tex1(ev(num*100, simp)), "\\,\\%"));
texput(aspercent, texaspercent);

/* bars for means of X and Y*/
texput(Xbar, "\\bar{X}");
texput(Ybar, "\\bar{Y}");

texput(thetaNH, "\\theta^{(0)}");

/* chi^2 quantiles */
texput(chi2_quantile, lambda([e], block([q, dof],
  [q, dof]: args(e),
  sconcat("\\chi^2_{", tex1(dof), "; ", tex1(1-q), "}"))));

nn: rand ([
  [  80, 120, 100,  90,  90, 120],
  [  70, 120, 150,  90, 190, 195],
  [  90, 115, 115,  90, 100,  85]
]);

kk: length (nn);
alpha: 0.05;

theta0: 1 / kk;
total: sum (nn[j], j, 1, kk);

statistic: sum ((nn[i] - total*theta0)^2 / (total * theta0), i, 1, kk);
critical: quantile_chi2 (1 - alpha, kk - 1);

simp: false;
chi2TestStatistic: sum((N[i] - n*thetaNH[i])^2 / (n*thetaNH[i]), i, 1, k);
statisticChoice: block([vals, correct, incorrect],
  correct: [X = chi2TestStatistic],
  incorrect: [
  T =&#8239;sum(X[i] - mu[0], i, 1, n)/sqrt(n*s_X^2),
  T = (n-1)* s_X^2 / sigma_0^2,
  T = (Ybar - Xbar) / sqrt((1/m + 1/n) *s_P^2),
  F = s_Y^2 / s_X^2],
  vals: makelist(i, i, ev(length(correct) + length(incorrect), simp)),
  zip_with(lambda([a, b],
      [a, if a <= length(correct) then true else false, stack_disp(b,"id")]),
    vals, append(correct, incorrect)));
simp:true;
tr(de,en):=castext("[[lang code='de']]{@de@}[[/lang]][[lang code='en']]{@en@}[[/lang]]");
test_result: [
[tr("Hypothese verwerfen","discard hypothesis"), is(statistic >= critical) ],
[tr("Hypothese nicht verwerfen","don't discard hypothesis"), is(statistic < critical)]];

testContext: block([vals, correct, incorrect],
  correct: [
    tr(sconcat("Die Wahrscheinlichkeit, einen fairen W&#252;rfel f&#252;r unfair zu halten, ist h&#246;chstens ", stack_disp(aspercent(alpha), "i"), "."),
       sconcat("The probability to consider a fair die as unfair is at most ", stack_disp(aspercent(alpha), "i"), ".")),      
    tr(sconcat("Bei diesem Verfahren halten wir in ", stack_disp(aspercent(alpha), "i"), " der F&#228;lle einen fairen W&#252;rfel f&#252;r unfair."),
       sconcat("In this procedure, we consider a fair die as unfair in ", stack_disp(aspercent(alpha), "i"), " of all cases."))
  ],
  incorrect: [
    tr(sconcat("Die Wahrscheinlichkeit, dass wir eine Fehlentscheidung treffen, ist h&#246;chstens ", stack_disp(aspercent(alpha), "i"), "."),
       sconcat("The probability of a wrong decision is at most ", stack_disp(aspercent(alpha), "i"), ".")),
    tr(sconcat("Die Wahrscheinlichkeit, einen unfairen W&#252;rfel zu entdecken, ist mindestens ", stack_disp(aspercent(1 - alpha), "i"), "."),
       sconcat("The probability to detect an unfair die is at least", stack_disp(aspercent(1 - alpha), "i"), "."))
  ],
  vals: makelist(i, i, ev(length(correct) + length(incorrect), simp)),
  zip_with(lambda([a, b],
      [a, if a <= length(correct) then true else false, b]),
    vals, append(correct, incorrect)));

Tans1: random_permutation (statisticChoice);
Tans2: statistic;
Tans3: decimalplaces(critical,1);
Tans3h1: random_permutation(
  block([vals, correct, incorrect],
    correct: [
    tr("Chi-Quadrat-Verteilung","chi-square distribution")
    ],
    incorrect: [
    tr("Normalverteilung","normal distribution"),
    tr("Student-t-Verteilung","student-t distribution"),
    tr("Binomialverteilung","binomial distribution"),
    tr("Exponentialverteilung","exponential distribution")],
    vals: makelist(i, i, ev(length(correct) + length(incorrect), simp)),
    zip_with(lambda([a, b],
        [a, if a <= length(correct) then true else false, b]),
      vals, append(correct, incorrect))));
Tans4: random_permutation (test_result);
Tans5: random_permutation (testContext);
