%emode:false;

/* Anzahl der gedruckten Nachkommastellen: */
fpprintprec: 4; 

/* Funktion f&#252;r das arithmetische Mittel einer Liste: */
mean2(L):=apply("+",L)/length(L);

/* &#220;berpr&#252;ft, ob eine Liste oder Menge nur ganze Zahlen enth&#228;lt und gibt true oder false zur&#252;ck: */
ganzzahlp(L):=freeof(false,makelist(integerp(L[k]),k,1,length(L))); 

/* &#220;berpr&#252;ft, ob eine Liste oder Menge L nur nicht-negative Zahlen enth&#228;lt und gibt true oder false zur&#252;ck: */
nichtnegativp(L):=freeof(false,makelist(not(is(L[k]<0)),k,1,length(L)));

/* In der Aufgabe angezeigte Jahreszahlen &#8211; ggf. aktualisieren! */
aktuellstes_Jahr: 2021;
Jahre: reverse(makelist(aktuellstes_Jahr-i,i,0,4)); 

/* Optionen f&#252;r die Aufgabe zur Verteilung: */
Liste2: [
    [1,true,"Poisson-Verteilung"],
    [2,false,"Binomialverteilung"],
    [3,false,"Bernoulli-Verteilung"],
    [4,false,"Laplace-Verteilung"],
    [5,false,"hypergeometrische Verteilung"],
    [6,false,"geometrische Verteilung"]
];
Liste2: random_permutation(Liste2);
TA2: 1;

/*
  In dieser Aufgabe rechnen die Studierenden mit selbst recherchierten
  Daten. Daher sind die korrekten L&#246;sungen noch nicht bekannt. Die
  folgenden Variablen sind nur f&#252;r die Musterl&#246;sungen, die f&#252;r die
  Eingabefelder definiert werden m&#252;ssen. (Annahme: ans1 = [1,2,3,4,5])
*/

/* Beispieldaten f&#252;r die Musterl&#246;sung: */
beispieldaten: [1,2,3,4,5];

/* Parameter der Verteilung f&#252;r die Misterl&#246;sung: */
lambda_bsp: decimaplaces(apply("+",beispieldaten)/length(beispieldaten),2);

/* Wahrscheinlichkeitsfunktion f&#252;r die Misterl&#246;sung: */
wfkt_bsp(k):=exp(-lambda_bsp)*lambda_bsp^k/k!;

/* Wahrscheinlichkeit P(X&#8804;2) f&#252;r die Misterl&#246;sung: */
wsk_bsp: wfkt_bsp(0)+wfkt_bsp(1)+wfkt_bsp(2);

