%emode:false;

/* Wahrscheinlichkeitsfunktion der Poisson-Verteilung: */
poisson_vert(k, lambda):=exp(-1*lambda)*(lambda^k)/k!;

/* Parameter n und p: */
n1: rand([1000, 2000, 3000, 4000]);
p1: if n1=3000 then rand([1/1000, 1/2000, 1/3000, 1/4000]) else rand([1/1000, 1/2000, 1/4000]);

/* Optionen in Dropdownliste zur Verteilung: */
Liste: [
    [1,true,"Poisson-Verteilung"],
    [2,false,"Binomialverteilung"],
    [3,false,"Geometrische Verteilung"],
    [4,false,"Hypergeometrische Verteilung"],
    [5,false,"Bernoulli-Verteilung"],
    [6,false,"Laplace-Verteilung"]
]; 
Liste: random_permutation(Liste);

/* Parameter der Verteilung: */
TA1: n1 * p1;
TA1_dp: if integerp(TA1) then round(TA1) else decimalplaces(TA1,2);

/* Wahrscheinlichkeit, dass im kommenden Jahr mindestens ein Sturmschaden eintritt: */
TA2: 1 - poisson_vert(0, n1 * p1);

/* Wahrscheinlichkeitsfunktion: */
TA2_z1: poisson_vert(k,TA1);
TA2_z1_float: poisson_vert(k,TA1_dp);

/* Optionen in Dropdownliste zur Formel f&#252;r die WSK der Gegenwahrscheinlichkeit: */
Liste2: [
    [1, true," \\(1-P(A)\\)"],
    [2, false," \\(\\frac{1}{P(A)}\\)"],
    [3, false, "\\((P(A))^c\\)"],
    [4, false," \\( \\frac{\\#A}{\\#\\Omega} \\)"]
];
Liste2: random_permutation(Liste2);
